import React, { useState } from 'react';
import { createUser } from '../utils/api';
import '../styles/adminDashboard.css';


const AdminDashboard = () => {
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      if (newUsername && newPassword) {
        await createUser(newUsername, newPassword);  // Call createUser with valid strings
        setSuccessMessage(`User ${newUsername} added successfully.`);
        setNewUsername('');  // Clear the input
        setNewPassword('');  // Clear the input
      } else {
        setErrorMessage('Username and password are required.');
      }
    } catch (error) {
      const backendMessage = error.response && error.response.data
        ? JSON.stringify(error.response.data)
        : error.message;
      setErrorMessage(`Failed to add user. ${backendMessage}`);
      setSuccessMessage('');  // Clear success message
    }
  };
  

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <form onSubmit={handleAddUser}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={newUsername}
            onChange={(e) => setNewUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add User</button>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}
      </form>
    </div>
  );
};

export default AdminDashboard;
