import React from 'react';
import TodoList from './TodoList';
import CourseInputForm from './CourseInputForm';
import usePersistentState from '../hooks/usePersistentState';

const Dashboard = () => {
  const [courses, setCourses] = usePersistentState('courses', []);
  const [tasks, setTasks] = usePersistentState('tasks', {});

  // Handle course submission
  const handleCoursesSubmit = (newCourses) => {
    setCourses(newCourses);
  };

  // Add new task
  const addTask = (course, category, task) => {
    setTasks(prevTasks => {
      const courseTasks = prevTasks[course] || {};
      const updatedCategoryTasks = courseTasks[category]
        ? [...courseTasks[category], task]
        : [task];
      return {
        ...prevTasks,
        [course]: {
          ...courseTasks,
          [category]: updatedCategoryTasks
        }
      };
    });
  };

  // Reset courses and tasks
  const handleReset = () => {
    if (window.confirm('Are you sure you want to reset your courses and tasks?')) {
      setCourses([]);  // Clear the courses
      setTasks({});    // Clear tasks
      localStorage.removeItem('courses'); // Clear local storage
      localStorage.removeItem('tasks');   // Clear local storage
    }
  };

  // If there are no courses, show the course input form
  if (courses.length === 0) {
    return (
      <div>
        <h2>No courses added yet</h2>
        <CourseInputForm onSubmit={handleCoursesSubmit} />
      </div>
    );
  }

  // Otherwise, show the dashboard with the reset option
  return (
    <div>
      <h2>Your Dashboard</h2>
      <button onClick={handleReset}>Reset Courses and Tasks</button>

      {courses.map(course => (
        <div key={course}>
          <h3>{course}</h3>
          <TodoList course={course} tasks={tasks[course]} addTask={addTask} />
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
