import React, { useState } from 'react';
import TodoList from './TodoList';
import CourseInputForm from './CourseInputForm';
import AddTaskForm from './AddTaskForm';
import usePersistentState from '../hooks/usePersistentState';

const Dashboard = () => {
  const [courses, setCourses] = usePersistentState('courses', []);
  const [tasks, setTasks] = usePersistentState('tasks', {});
  const [selectedCourse, setSelectedCourse] = useState('');
  const [showAddTaskForm, setShowAddTaskForm] = useState(false);

  // Handle course submission
  const handleCoursesSubmit = (newCourses) => {
    setCourses(newCourses);
  };

  // Add new task
  const addTask = (course, category, task) => {
    setTasks(prevTasks => {
      const courseTasks = prevTasks[course] || {};
      const updatedCategoryTasks = courseTasks[category]
        ? [...courseTasks[category], task]
        : [task];
      return {
        ...prevTasks,
        [course]: {
          ...courseTasks,
          [category]: updatedCategoryTasks
        }
      };
    });
  };

  // Handle reset
  const handleReset = () => {
    if (window.confirm('Are you sure you want to reset your courses and tasks?')) {
      setCourses([]);
      setTasks({});
      localStorage.removeItem('courses');
      localStorage.removeItem('tasks');
    }
  };

  // Show add task form
  const handleShowAddTaskForm = () => {
    setShowAddTaskForm(true);
  };

  // Handle adding a task
  const handleAddTask = (course, category, task) => {
    addTask(course, category, task);
    setShowAddTaskForm(false);
  };

  // If there are no courses, show the course input form
  if (courses.length === 0) {
    return (
      <div>
        <h2>No courses added yet</h2>
        <CourseInputForm onSubmit={handleCoursesSubmit} />
      </div>
    );
  }

  // Otherwise, show the dashboard with the reset option and add task button
  return (
    <div>
      <h2>Your Dashboard</h2>
      <button onClick={handleReset}>Reset Courses and Tasks</button>
      <button onClick={handleShowAddTaskForm}>Add Task</button>

      {showAddTaskForm && (
        <AddTaskForm
          courses={courses}
          onAddTask={handleAddTask}
          onClose={() => setShowAddTaskForm(false)}
        />
      )}

      {courses.map(course => (
        <div key={course}>
          <h3>{course}</h3>
          <TodoList course={course} tasks={tasks[course]} />
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
