import React, { useState, useEffect } from 'react';

// This is a simplified example. Ensure you adapt it based on your actual use case and data structure.
const TodoList = ({ courseNames }) => {
  // Ensure courseNames is initialized
  const [todos, setTodos] = useState([]);

  useEffect(() => {
    if (courseNames) {
      // Initialize todos based on courseNames
      setTodos(courseNames.map(course => ({
        course,
        tasks: { assignments: [], examDates: [], studyGoals: [] }
      })));
    }
  }, [courseNames]);

  if (!courseNames || courseNames.length === 0) {
    return <p>No courses available. Please add courses.</p>;
  }

  // Handle tasks and rendering
  return (
    <div>
      {todos.map((todo, index) => (
        <div key={index}>
          <h2>{todo.course}</h2>
          <h3>Assignments</h3>
          {/* Render assignments */}
          {/* Add more sections for examDates and studyGoals */}
        </div>
      ))}
    </div>
  );
};

export default TodoList;
