import React, { useEffect, useState } from 'react';
import { fetchCourses, createCourse } from '../utils/api';
import AddTaskForm from './AddTaskForm';
import TodoList from './TodoList';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [courses, setCourses] = useState([]);
  const [courseName, setCourseName] = useState('');
  const [showAddTaskForm, setShowAddTaskForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const loadCourses = async () => {
      if (!userId) {
        setError('User ID not found. Please log in again.');
        setLoading(false);
        return;
      }
      try {
        setLoading(true);
        const courseData = await fetchCourses(userId);
        setCourses(courseData);
      } catch (error) {
        setError('Failed to fetch courses. Please try again.');
        console.error('Failed to fetch courses:', error);
      } finally {
        setLoading(false);
      }
    };
    loadCourses();
  }, [userId]);

  const handleCreateCourse = async (e) => {
    e.preventDefault();
    if (!courseName.trim()) {
      setError('Course name cannot be empty.');
      return;
    }
    try {
      const newCourse = await createCourse(userId, courseName);
      setCourses([...courses, newCourse]);
      setCourseName('');
      setError(null);  // Clear any error on success
    } catch (error) {
      setError('Failed to create course. Please try again.');
      console.error('Failed to create course:', error);
    }
  };

  const handleTaskAdded = (newTask) => {
    // Update the tasks in the corresponding course
    setCourses(prevCourses =>
      prevCourses.map(course => {
        if (course.id === newTask.course_id) {
          return {
            ...course,
            tasks: [...(course.tasks || []), newTask],  // Add the new task to the course's tasks
          };
        }
        return course;
      })
    );
  };

  return (
    <div className="dashboard">
      <h2>Your Dashboard</h2>
      
      {/* Course Creation Form */}
      <form onSubmit={handleCreateCourse} className="create-course-form">
        <input
          type="text"
          placeholder="Course Name"
          value={courseName}
          onChange={(e) => setCourseName(e.target.value)}
        />
        <button type="submit">Add Course</button>
      </form>

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      {/* Add Task Button */}
      <button onClick={() => setShowAddTaskForm(true)} className="add-task-button">
        Add Task
      </button>

      {/* Task Form */}
      {showAddTaskForm && (
        <AddTaskForm
          courses={courses}
          onClose={() => setShowAddTaskForm(false)}
          onTaskAdded={handleTaskAdded}  // Pass the handler to update tasks
        />
      )}

      {/* Loading Indicator */}
      {loading ? (
        <p>Loading courses...</p>
      ) : (
        <div className="course-list">
          {courses.length > 0 ? (
            courses.map((course) => (
              <div key={course.id} className="course-item">
                <h3>{course.name}</h3>
                <TodoList course={course} />
              </div>
            ))
          ) : (
            <p>No courses available. Please add a course.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
