import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import LoginPage from './components/LoginPage';
import AdminDashboard from './components/AdminDashboard';
import usePersistentState from './hooks/usePersistentState';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [courses, setCourses] = usePersistentState('courses', []);
  const [users] = usePersistentState('users', { admin: 'admin123' });

  const handleLogin = (username, password) => {
    if (users[username] && users[username] === password) {
      setLoggedIn(true);
      if (username === 'admin') {
        setIsAdmin(true);
      }
    } else {
      alert('Invalid credentials');
    }
  };

  const handleCoursesSubmit = (newCourses) => {
    setCourses(newCourses);
  };

  const handleResetCourses = () => {
    setCourses([]);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
        <Route
          path="/dashboard"
          element={loggedIn && !isAdmin ? <Dashboard courses={courses} onResetCourses={handleResetCourses} /> : <LoginPage onLogin={handleLogin} />}
        />
        <Route
          path="/admin"
          element={loggedIn && isAdmin ? <AdminDashboard /> : <LoginPage onLogin={handleLogin} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
