import React, { useState, useEffect } from 'react';
import { fetchCourses } from '../utils/api';
import CourseInputForm from './CourseInputForm';

const Dashboard = () => {
  const [courses, setCourses] = useState([]);
  const userId = localStorage.getItem('userId');  // Get user ID from local storage

  const loadCourses = async () => {
    try {
      const courseData = await fetchCourses(userId);
      setCourses(courseData);  // Update the state with the fetched courses
    } catch (error) {
      console.error('Failed to fetch courses:', error);
    }
  };

  // Fetch courses when the component mounts
  useEffect(() => {
    loadCourses();
  }, []);

  const handleCourseAdded = () => {
    loadCourses();  // Reload courses after a new course is added
  };

  return (
    <div>
      <h2>Your Courses</h2>
      <CourseInputForm userId={userId} onCourseAdded={handleCourseAdded} />  {/* Pass callback to refresh */}
      
      <div className="course-list">
        {courses.length > 0 ? (
          courses.map(course => (
            <div key={course.id}>
              <h3>{course.name}</h3>
            </div>
          ))
        ) : (
          <p>No courses available. Please add a course.</p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
