import React, { useEffect, useState } from 'react';
import { fetchCourses, createCourse, fetchTasks } from '../utils/api';
import AddTaskForm from './AddTaskForm';
import TodoList from './TodoList';
import '../styles/dashboard.css';

const Dashboard = () => {
  const [courses, setCourses] = useState([]);
  const [tasks, setTasks] = useState([]);  // Store tasks
  const [courseName, setCourseName] = useState('');
  const [showAddTaskForm, setShowAddTaskForm] = useState(false);
  const [loading, setLoading] = useState(true);  // Loading state
  const [error, setError] = useState(null);  // Error state
  const userId = localStorage.getItem('userId');  // Get user ID from localStorage

  // Fetch courses and tasks when component loads
  useEffect(() => {
    const loadCoursesAndTasks = async () => {
      if (!userId) {
        setError('User ID not found. Please log in again.');
        setLoading(false);
        return;
      }
      try {
        setLoading(true);

        // Fetch courses for the logged-in user
        const courseData = await fetchCourses(userId);  
        setCourses(courseData);

        // Fetch tasks for the logged-in user
        const taskData = await fetchTasks(userId);  
        setTasks(taskData);
      } catch (error) {
        setError('Failed to fetch courses and tasks. Please try again.');
        console.error('Failed to fetch courses or tasks:', error);
      } finally {
        setLoading(false);
      }
    };
    loadCoursesAndTasks();
  }, [userId]);

  const handleCreateCourse = async (e) => {
    e.preventDefault();
    if (!courseName.trim()) {
      setError('Course name cannot be empty.');
      return;
    }
    try {
      const newCourse = await createCourse(userId, courseName);  // Pass user ID and course name
      setCourses([...courses, newCourse]);
      setCourseName('');
      setError(null);  // Clear any error on success
    } catch (error) {
      setError('Failed to create course. Please try again.');
      console.error('Failed to create course:', error);
    }
  };

  // Function to refresh tasks after adding a new task
  const refreshTasks = async () => {
    try {
      const updatedTasks = await fetchTasks(userId);
      setTasks(updatedTasks);
    } catch (error) {
      console.error('Failed to refresh tasks:', error);
    }
  };

  return (
    <div className="dashboard">
      <h2>Your Dashboard</h2>
      
      {/* Course Creation Form */}
      <form onSubmit={handleCreateCourse} className="create-course-form">
        <input
          type="text"
          placeholder="Course Name"
          value={courseName}
          onChange={(e) => setCourseName(e.target.value)}
        />
        <button type="submit">Add Course</button>
      </form>

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      {/* Add Task Button */}
      <button onClick={() => setShowAddTaskForm(true)} className="add-task-button">
        Add Task
      </button>

      {/* Task Form */}
      {showAddTaskForm && (
        <AddTaskForm 
          courses={courses} 
          onClose={() => {
            setShowAddTaskForm(false);
            refreshTasks();  // Refresh tasks after closing the task form
          }} 
        />
      )}

      {/* Loading Indicator */}
      {loading ? (
        <p>Loading courses and tasks...</p>
      ) : (
        <div className="course-list">
          {courses.length > 0 ? (
            courses.map((course) => (
              <div key={course.id} className="course-item">
                <h3>{course.name}</h3>

                {/* Display tasks for this course */}
                <TodoList 
                  course={course} 
                  tasks={tasks.filter(task => task.course && task.course.id === course.id)}  // Ensure tasks are only shown for this course
                />
              </div>
            ))
          ) : (
            <p>No courses available. Please add a course.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
