import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/scheduler/';

// User Login (admin or regular user)
export const loginUser = async (username, password) => {
  try {
    const response = await axios.get(`${API_URL}users/`);
    const users = response.data;

    // Check if the username and password match any user
    const user = users.find(u => u.username === username && u.password === password);
    if (!user) {
      throw new Error('Invalid credentials');
    }
    return user;  // Return user data, no token involved
  } catch (error) {
    console.error('Login error:', error);
    throw error;
  }
};

// Fetch all courses for a user
export const fetchCourses = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}courses/`, { params: { user_id: userId } });
    return response.data;
  } catch (error) {
    console.error('Error fetching courses:', error);
    throw error;
  }
};

// Create a new course for the user
export const createCourse = async (userId, courseName) => {
  try {
    const response = await axios.post(`${API_URL}courses/`, { name: courseName, user_id: userId });
    return response.data;
  } catch (error) {
    console.error('Error creating course:', error);
    throw error;
  }
};

// Fetch all tasks for a user
export const fetchTasks = async (userId) => {
  try {
    const response = await axios.get(`${API_URL}tasks/`, { params: { user_id: userId } });
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

// Create a new task
export const createTask = async (courseId, category, description) => {
  try {
    const response = await axios.post(`${API_URL}tasks/`, { course_id: courseId, category, description });
    return response.data;
  } catch (error) {
    console.error('Error creating task:', error);
    throw error;
  }
};
