// src/components/CourseInputForm.jsx
import React, { useState } from 'react';

const CourseInputForm = ({ onCoursesSubmit }) => {
  const [numCourses, setNumCourses] = useState(0);
  const [courses, setCourses] = useState([]);

  const handleNumCoursesChange = (e) => {
    setNumCourses(Number(e.target.value));
    setCourses(Array(Number(e.target.value)).fill(''));
  };

  const handleCourseNameChange = (index, value) => {
    const updatedCourses = [...courses];
    updatedCourses[index] = value;
    setCourses(updatedCourses);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onCoursesSubmit(courses);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Number of Courses:
        <input
          type="number"
          value={numCourses}
          onChange={handleNumCoursesChange}
          min="1"
        />
      </label>

      {courses.map((course, index) => (
        <div key={index}>
          <label>
            Course {index + 1} Name:
            <input
              type="text"
              value={course}
              onChange={(e) => handleCourseNameChange(index, e.target.value)}
            />
          </label>
        </div>
      ))}

      <button type="submit">Submit</button>
    </form>
  );
};

export default CourseInputForm;
