import React, { useState } from 'react';
import Dashboard from './components/Dashboard';
import LoginPage from './components/LoginPage';
import AdminDashboard from './components/AdminDashboard';
import usePersistentState from './hooks/usePersistentState';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [courses, setCourses] = usePersistentState('courses', []);
  const [users] = usePersistentState('users', { admin: 'admin123' });

  const handleLogin = (username, password) => {
    if (users[username] && users[username] === password) {
      setLoggedIn(true);
      if (username === 'admin') {
        setIsAdmin(true);
      }
    } else {
      alert('Invalid credentials');
    }
  };

  const handleCoursesSubmit = (newCourses) => {
    setCourses(newCourses);
  };

  const handleResetCourses = () => {
    setCourses([]);
  };

  if (!loggedIn) {
    return <LoginPage onLogin={handleLogin} />;
  }

  if (isAdmin) {
    return <AdminDashboard />;
  }

  return (
    <Dashboard courses={courses} onResetCourses={handleResetCourses} />
  );
};

export default App;
