import React, { useEffect, useState } from 'react';
import { fetchTasks, deleteTask } from '../utils/api';
import '../styles/todoList.css';

const TodoList = ({ course }) => {
  const [tasks, setTasks] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const loadTasks = async () => {
      try {
        const taskData = await fetchTasks(userId);
        setTasks(taskData.filter(task => task.course.id === course.id));
      } catch (error) {
        console.error('Failed to fetch tasks', error);
      }
    };
    loadTasks();
  }, [userId, course.id]);

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error('Failed to delete task', error);
    }
  };

  return (
    <div className="todo-list">
      {tasks.length > 0 ? (
        tasks.map((task) => (
          <div key={task.id} className="task-item">
            <p>{task.category}: {task.description}</p>
            <button onClick={() => handleDeleteTask(task.id)}>Delete</button>
          </div>
        ))
      ) : (
        <p>No tasks available for this course.</p>
      )}
    </div>
  );
};

export default TodoList;
