import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000/api/scheduler/';

// Helper function to set authorization headers with token
const getAuthHeaders = (token) => ({
  headers: {
    Authorization: `Bearer ${token}`,
  },
});

// User Login (admin or regular user)
export const loginUser = async (username, password) => {
  try {
    const response = await axios.get(`${API_URL}users/`, {
      auth: {
        username,
        password,
      },
    });
    return response.data; // Should return user data with token
  } catch (error) {
    console.error('Login error:', error);
    throw error;
  }
};

// Fetch all courses for the authenticated user
export const fetchCourses = async (token) => {
  try {
    const response = await axios.get(`${API_URL}courses/`, getAuthHeaders(token));
    return response.data;
  } catch (error) {
    console.error('Error fetching courses:', error);
    throw error;
  }
};

// Create a new course for the authenticated user
export const createCourse = async (token, courseName) => {
  try {
    const response = await axios.post(
      `${API_URL}courses/`,
      { name: courseName },
      getAuthHeaders(token)
    );
    return response.data;
  } catch (error) {
    console.error('Error creating course:', error);
    throw error;
  }
};

// Fetch all tasks for the authenticated user
export const fetchTasks = async (token) => {
  try {
    const response = await axios.get(`${API_URL}tasks/`, getAuthHeaders(token));
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

// Create a new task in a course
export const createTask = async (token, courseId, category, description) => {
  try {
    const response = await axios.post(
      `${API_URL}tasks/`,
      {
        course_id: courseId,
        category,
        description,
      },
      getAuthHeaders(token)
    );
    return response.data;
  } catch (error) {
    console.error('Error creating task:', error);
    throw error;
  }
};

// Delete a task by its ID
export const deleteTask = async (token, taskId) => {
  try {
    await axios.delete(`${API_URL}tasks/${taskId}`, getAuthHeaders(token));
  } catch (error) {
    console.error('Error deleting task:', error);
    throw error;
  }
};
export const createUser = async (token, username, password) => {
  try {
    const response = await axios.post(
      `${API_URL}users/`,
      { username, password },  // Send the username and password in the request body
      getAuthHeaders(token)  // Add authentication header
    );
    return response.data;
  } catch (error) {
    console.error('Error creating user:', error);
    throw error;
  }
};
