import React, { useState, useEffect } from 'react';
import { saveToLocalStorage, loadFromLocalStorage } from '../utils/localStorage';

const CourseInputForm = ({ username, onCoursesSubmit }) => {
  const [numCourses, setNumCourses] = useState(0);
  const [courseNames, setCourseNames] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    const userData = loadFromLocalStorage('users')[username];
    if (userData && userData.courses.length > 0) {
      setCourseNames(userData.courses);
      setSubmitted(true);
    }
  }, [username]);

  const handleCourseChange = (index, value) => {
    const updatedCourses = [...courseNames];
    updatedCourses[index] = value;
    setCourseNames(updatedCourses);
  };

  const handleSubmit = () => {
    const users = loadFromLocalStorage('users');
    users[username].courses = courseNames;
    saveToLocalStorage('users', users);
    setSubmitted(true);
    onCoursesSubmit(courseNames);
  };

  const handleReset = () => {
    setNumCourses(0);
    setCourseNames([]);
    setSubmitted(false);
  };

  return (
    <div>
      {!submitted ? (
        <div>
          <input
            type="number"
            placeholder="Number of courses"
            value={numCourses}
            onChange={(e) => setNumCourses(parseInt(e.target.value))}
          />
          {Array.from({ length: numCourses }).map((_, i) => (
            <input
              key={i}
              type="text"
              placeholder={`Course ${i + 1}`}
              value={courseNames[i] || ''}
              onChange={(e) => handleCourseChange(i, e.target.value)}
            />
          ))}
          <button onClick={handleSubmit}>Submit Courses</button>
        </div>
      ) : (
        <div>
          <h2>Courses Added:</h2>
          <ul>
            {courseNames.map((course, i) => (
              <li key={i}>{course}</li>
            ))}
          </ul>
          <button onClick={handleReset}>Reset Courses</button>
        </div>
      )}
    </div>
  );
};

export default CourseInputForm;
