import React, { useState } from 'react';
import { createUser } from '../utils/api';  // Import the createUser function

const AdminDashboard = () => {
  const [newUsername, setNewUsername] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const token = localStorage.getItem('token');  // Get token for API authentication

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      await createUser(token, newUsername, newPassword);  // Call the createUser function
      setSuccessMessage(`User ${newUsername} added successfully.`);
      setNewUsername('');
      setNewPassword('');
      setErrorMessage('');  // Clear any previous errors
    } catch (error) {
      console.error('Failed to add user:', error.response ? error.response.data : error.message);
      setErrorMessage(`Failed to add user. ${error.response ? error.response.data : error.message}`);
      setSuccessMessage('');  // Clear success message
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <form onSubmit={handleAddUser}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={newUsername}
            onChange={(e) => setNewUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add User</button>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        {successMessage && <p className="success-message">{successMessage}</p>}
      </form>
    </div>
  );
};

export default AdminDashboard;
